import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import LanguageDetector from "i18next-browser-languagedetector";

const utilityPl = {
  theme: "Motyw",
  language: "Język",
};
const utilityEn = {
  theme: "Theme",
  language: "Language",
};

const resources = {
  en: {
    translation: {
      ...utilityEn,
    },
  },
  pl: {
    translation: {
      ...utilityPl,
    },
  },
};

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    resources,
    fallbackLng: "en",
    interpolation: {
      escapeValue: false,
    },
  });

export default i18n;
