import { css } from "styled-components";

export const colorMatrix = {
  gray: {
    darkest: "#000000",
    lightest: "#ffffff",
  },
  green: {
    basic: "green",
  },
  red: {
    basic: "red",
  },
};

export const defaultBorderRadius = css`
  border-radius: 0.3em;
`;

export const defaultBoxShadow = css`
  box-shadow: 0 1em 2em rgba(0, 0, 0, 0.274);
`;
