import styled, {
  ThemedStyledProps,
  css,
  FlattenSimpleInterpolation,
} from "styled-components";
import {
  defaultBorderRadius,
  defaultBoxShadow,
  roundBorderRadius,
} from "../../styleConsts";
import { Theme } from "../../theme";

enum ButtonVariant {
  PRIMARY = "PRIMARY",
  SECONDARY = "SECONDARY",
  POSITIVE = "POSITIVE",
  NEGATIVE = "NEGATIVE",
}

interface ButtonProps {
  round?: boolean;
  variant?: ButtonVariant;
}

const Button = styled.button<ButtonProps>`
  ${defaultBoxShadow};
  ${({ round }) => (round ? roundBorderRadius : defaultBorderRadius)}

  height: 4em;
  width: 4em;
  font-size: 2em;

  ${(props) => {
    switch (props.variant) {
      case ButtonVariant.SECONDARY:
        return secondaryVariantStyle(props);
      case ButtonVariant.POSITIVE:
        return positiveVariantStyle(props);
      case ButtonVariant.NEGATIVE:
        return negativeVariantStyle(props);
      default:
        return primaryVariantStyle(props);
    }
  }}
`;

export { Button, ButtonVariant };
