import styled, {
  ThemedStyledProps,
  css,
  FlattenSimpleInterpolation,
} from "styled-components";
import {
  defaultBorderRadius,
  defaultBoxShadow,
  roundBorderRadius,
} from "../styleConsts";
import { Theme } from "../theme";

enum ButtonVariant {
  PRIMARY = "PRIMARY",
  SECONDARY = "SECONDARY",
}

interface ButtonProps {
  round?: boolean;
  variant?: ButtonVariant;
}

const primaryVariantStyle = (
  props: ThemedStyledProps<ButtonProps, Theme>
): FlattenSimpleInterpolation => css`
  border: none;
  color: ${props.theme.background};
  background-color: ${props.theme.primary};
`;

const secondaryVariantStyle = (
  props: ThemedStyledProps<ButtonProps, Theme>
): FlattenSimpleInterpolation => css`
  border: 0.3em solid ${props.theme.primary};
  color: ${props.theme.primary};
  background-color: ${props.theme.background};
`;

const Button = styled.button<ButtonProps>`
  ${defaultBoxShadow};
  ${(round) => (round ? roundBorderRadius : defaultBorderRadius)}

  height: 4em;
  width: 4em;
  font-size: 2em;

  ${(props) => {
    if (props.variant === ButtonVariant.PRIMARY) {
      return primaryVariantStyle(props);
    }
    return secondaryVariantStyle(props);
  }}
`;

export { Button, ButtonVariant };
