import styled, {
  ThemedStyledProps,
  css,
  FlattenSimpleInterpolation,
} from "styled-components";
import {
  defaultBorderRadius,
  defaultBoxShadow,
  roundBorderRadius,
} from "../styleConsts";
import { Theme } from "../theme";

enum ButtonVariant {
  PRIMARY = "PRIMARY",
  SECONDARY = "SECONDARY",
  POSITIVE = "POSITIVE",
  NEGATIVE = "NEGATIVE",
}

interface ButtonProps {
  round?: boolean;
  variant?: ButtonVariant;
}

const primaryVariantStyle = (
  props: ThemedStyledProps<ButtonProps, Theme>
): FlattenSimpleInterpolation => css`
  border: none;
  color: ${props.theme.background};
  background-color: ${props.theme.primary};
`;

const positiveVariantStyle = (
  props: ThemedStyledProps<ButtonProps, Theme>
): FlattenSimpleInterpolation => css`
  border: none;
  color: ${props.theme.background};
  background-color: ${props.theme.positive};
`;

const negativeVariantStyle = (
  props: ThemedStyledProps<ButtonProps, Theme>
): FlattenSimpleInterpolation => css`
  border: none;
  color: ${props.theme.background};
  background-color: ${props.theme.negative};
`;

const secondaryVariantStyle = (
  props: ThemedStyledProps<ButtonProps, Theme>
): FlattenSimpleInterpolation => css`
  border: 0.3em solid ${props.theme.primary};
  color: ${props.theme.primary};
  background-color: ${props.theme.background};
`;

const Button = styled.button<ButtonProps>`
  ${defaultBoxShadow};
  ${(round) => (round ? roundBorderRadius : defaultBorderRadius)}

  height: 4em;
  width: 4em;
  font-size: 2em;

  ${(props) => {
    switch (props.variant) {
      case ButtonVariant.SECONDARY:
        return secondaryVariantStyle(props);
      case ButtonVariant.POSITIVE:
        return positiveVariantStyle(props);
      case ButtonVariant.NEGATIVE:
        return negativeVariantStyle(props);
      default:
        return primaryVariantStyle(props);
    }
  }}
`;

export { Button, ButtonVariant };
